'use strict';

var process = require('process');
var expect = require('chai').expect;
var SchemaClass = require('./SchemaClass').default;
//import SchemaClass from 'json-schema-class';

/** @test {SchemaClass} **/
describe('SchemaClass Public Methods', function () {

  it('is able create a basic validator class', function () {

    //ES6
    let Validator = new SchemaClass({
      "type": "string"
    });

    // Using a valid input
    let text = Validator.validate("Input Text");
    expect(text).to.equal("Input Text");

    // Using an invalid input
    expect(function () {
      Validator.validate(1)
    }).to.throw(Error);
  });

  it('is able to use extends', function () {
    class SimpleClass extends SchemaClass {
      constructor(text) {
        super({
          "type": "string",
          "enum": ["Text"]
        });

        this.validate(text);
      }
    }
    expect(SimpleClass).to.exist;

    let SimpleModel = new SimpleClass("Text");

    expect(SimpleModel).to.exist;
  });

  it('is able to create new instances with defaults', function () {
    class WithDefaults extends SchemaClass {
      constructor(id) {
        super({
          "$schema": "http://json-schema.org/draft-04/schema#",
          "id": "WithDefaults",
          "type": "object",
          "properties": {
            "id": {
              "type": "string"
            },
            "check": {
              "type": "string",
              "default": "This is a default value"
            }
          },
          "required": ["id"]
        });

        this.id = id;

        this.validate(this);
      }
    }

    //On the import side you can instantiate it like so
    let defaultTest = new WithDefaults("1");

    //Expect find the class model's id
    expect(defaultTest.id).to.exist;

    //Any defaults should exist after creating a new instance
    expect(defaultTest.check).to.exist;
  });

  it('is able to validate the model data', function () {
    class BadData extends SchemaClass {
      constructor(id) {
        super({
          "$schema": "http://json-schema.org/draft-04/schema#",
          "id": "BadData",
          "type": "object",
          "properties": {
            "id": {
              "type": "string"
            }
          },
          "required": ["id"]
        });

        /**
         * Do anything you need to do with the data model
         * You can create a custom Model placeholder or
         * validate the class directly like so
         *
         * let model = this;
         *  - or -
         * let model = {}
         */
        let model = this;
        model.id = 1;

        this.validate(model)
      }
    }

    expect(BadData).to.exist;

    expect(function () {
      new BadData(1)
    }).to.throw(Error);


  })
});

describe('SchemaClass Private Methods', function () {
  it('is able to throw a schema error using _schemaError', function () {
    let myObj = new SchemaClass();

    expect(myObj._schemaError).to.exist;
    expect(function () {
      myObj._schemaError()
    }).to.throw(Error);
    expect(function () {
      myObj._schemaError('Something Happening')
    }).to.throw(Error);

  });

  it('is able to get and set the schema', function () {
    let myObj = new SchemaClass();

    expect(myObj.getSchema).to.exist;

    expect(function () {
      myObj.getSchema()
    }).to.throw(Error);

    expect(myObj._setSchema).to.exist;

    expect(function () {
      myObj._setSchema()
    }).to.throw(Error);

    expect(function () {
      myObj._setSchema({
        "type": "string"
      })
    }).to.not.throw(Error);

    expect(myObj.schema).to.deep.equal({
      "type": "string"
    });

    expect(function () {
      myObj.getSchema()
    }).to.not.throw(Error);

    expect(myObj.getSchema()).to.deep.equal({
      "type": "string"
    });


  });

  it('is able to get and set a validator', function () {
    let myObj = new SchemaClass({
      "type": "string"
    });

    expect(myObj._getValidator).to.exist;

    let test = null;

    /**
     * The Validator will fail if setValidator is not triggered
     */
    expect(function () {
      test = myObj._getValidator()
    }).to.throw(Error);

    expect(function () {
      myObj._setValidator()
    }).to.not.throw(Error);

    expect(function () {
      test = myObj._getValidator()
    }).to.not.throw(Error);

    expect(test).to.exist;

    expect(myObj.schema).to.deep.equal({
      "type": "string"
    });

    expect(function () {
      myObj.validate("Input Text");
    }).to.not.throw(Error);

    expect(function () {
      myObj.validate(1);
    }).to.throw(Error);


    expect(function () {
      myObj._setValidator({
        "type": "number"
      })
    }).to.not.throw(Error);

    expect(function () {
      myObj.validate("Input Text");
    }).to.throw(Error);

    expect(function () {
      myObj.validate(1);
    }).to.not.throw(Error);

    expect(myObj.schema).to.deep.equal({
      "type": "number"
    });

  });

});

'use strict';

import merge from 'merge';
import events from 'events';
import Ajv from 'ajv';
let ajv = Ajv({allErrors: true, useDefaults: true});

/**
 * Where all things good come from! This Class uses JSON Schema Draft 4 to build
 *    basic models for the rest of the application. Please See json-schema.org
 *    and also ajv validator for the basics.
 *
 * @example
 *  import SchemaClass from 'json-schema-class';
 *  export MyClass extends 'SchemaClass'{
 *    constructor(){
 *      super({
 *        "title": "Example Schema",
 *        "type": "object",
 *        "properties": {
 *          "firstName": {
 *            "type": "string"
 *          },
 *          "lastName": {
 *            "type": "string"
 *          },
 *          "age": {
 *            "description": "Age in years",
 *            "type": "integer",
 *            "minimum": 0
 *          }
 *        },
 *        "required": ["firstName", "lastName"]
 *      });
 *
 *      let model = this;
 *      model.firstName = "My"
 *      model.lastName = "Class"
 *
 *      this.validate(model) //Validate model and add missing defaults
 *    )
 *  }
 */
export default class SchemaClass {
  /**
   * A valid JSON Schema for this class. You can find examples
   * at http://json-schema.org/examples.html. There is also a
   * generator for JSON data. http://jsonschema.net/#/
   * @example
   *  {
 *    "title": "Example Schema",
 *    "type": "object",
 *    "properties": {
 *      "firstName": {
 *        "type": "string"
 *      },
 *      "lastName": {
 *        "type": "string"
 *      },
 *      "age": {
 *        "description": "Age in years",
 *        "type": "integer",
 *        "minimum": 0
 *      }
 *    },
 *    "required": ["firstName", "lastName"]
 *  }
   *
   * @see http://json-schema.org/
   * @typedef {Object} Schema
   */

  /**
   * Accepts a JSON Schema pattern for Model Building
   * @param {Schema} schema Schema for the new Class
   */
  constructor(schema) {
    /**
     * The Schema for this Class
     * @type {Schema}
     * @see http://json-schema.org/
     * @abstract
     */
    this.schema = null;

    if (schema) this.schema = schema;

    /**
     * Class Validator - Instance of ajv.compile()
     * @see https://www.npmjs.com/package/ajv#compileobject-schema---functionobject-data
     * @abstract
     */
    this.validator = null;

    /**
     * Event Emitter
     * @see https://nodejs.org/api/events.html
     */
    this.events = new events.EventEmitter();

    /**
     * A Merge utility for JavaScript Objects
     * @see https://github.com/yeikos/js.merge
     */
    this.merge = merge;

  };


  /**
   * Throw Schema Error - Wrapper for generic schema errors
   * @param {Error} err Error to throw
   * @private
   */
  _schemaError = (err) => {
    if (err) throw new Error(err);
    throw new Error("Unknown Error......");
  };

  /**
   * Set the Current Schema
   * @param {Schema} schema JSON Schema 4
   * @private
   */
  _setSchema = (schema) => {
    if (schema) this.schema = schema;
    else this._schemaError(ReferenceError('Must Specify Schema!'))
  };

  /**
   *
   * @returns {null}
   * @private
   */
  _getValidator = () => {
    if (!this.validator) this._schemaError(new ReferenceError("Must run _setValidator first!"));
    return this.validator;
  };

  /**
   * Set a Schema Validator to the current instance
   * @returns {Object}
   * @private
   */
  _setValidator = (schema) => {
    if (schema) this._setSchema(schema);
    var validator = this.validator = ajv.compile(this.getSchema());
    return validator;
  };

  /**
   * Get the Current Schema
   * @returns {Schema}
   */
  getSchema = () => {
    if (!this.schema) this._schemaError();
    return this.schema;
  };
  
  /**
   * Validate data against the current schema
   * @param {Object} data
   * @returns {Object} data
   */
  validate(data) {
    if (!this.validator) this._setValidator();
    let valid = this._getValidator();
    if (valid && valid(data)) return data;
    else throw this._schemaError(valid.errors[0])
  };

}

